func TestEvm(t *testing.T) {
	jsonclient, err := client.NewJSONClient("", url)
	assert.Nil(t, err)

	// 部署合约
	code, err := types.FromHex(codes)
	assert.Nil(t, err)

	tx, err := CreateEvmContract(code, "", "evm-sdk-test", paraName, int32(addressID), int32(chainID))
	unsignTx := types.ToHex(types.Encode(tx))
	gas, err := QueryEvmGas(url, unsignTx, deployAddress)
	assert.Nil(t, err)
	fmt.Println("gas fee = ", gas)
	fee := GetProperFee(url)
	fmt.Println("proper fee = ", fee)
	UpdateTxFee(tx, gas, fee)
	err = SignTx(tx, deployPrivateKey, int32(addressID))
	assert.Nil(t, err)
	txhash := types.ToHexPrefix(tx.Hash())
	signTx := types.ToHexPrefix(types.Encode(tx))
	reply, err := jsonclient.SendTransaction(signTx)
	assert.Nil(t, err)
	assert.Equal(t, txhash, reply)
	fmt.Print("部署合约交易hash = ", txhash)
	time.Sleep(8 * time.Second)
	detail, err := jsonclient.QueryTransaction(txhash)
	assert.Nil(t, err)
	fmt.Println("; 执行结果 = ", detail.Receipt.Ty)

	// 计算合约地址
	contractAddress := GetContractAddr(deployAddress, strings.TrimPrefix(txhash, "0x"), url)
	//contractAddress := LocalGetContractAddr(deployAddress, tx.Hash(), int32(addressID))
	fmt.Println("部署好的合约地址 = " + contractAddress)

	length := 2
	// tokenId数组
	ids := make([]int, length)
	// 同一个tokenid发行多少份
	amounts := make([]int, length)
	// 每一个tokenid对应的URI信息（一般对应存放图片的描述信息，图片内容的一个url）
	uris := make([]string, length)
	for i := 0; i < length; i++ {
		ids[i] = 10000 + i
		amounts[i] = 100
		// 例子为了简化处理，让所有ID都固定一个地址，
		uris[i] = "http://www.baidu.com"
	}
	idStr, _ := json.Marshal(ids)
	amountStr, _ := json.Marshal(amounts)
	uriStr, _ := json.Marshal(uris)

	// 调用合约
	param := fmt.Sprintf("mint(%s,%s,%s,%s)", useraAddress, idStr, amountStr, uriStr)
	initNFT, err := EncodeParameter(abi, param)
	assert.Nil(t, err)
	tx, err = CallEvmContract(initNFT, "", 0, contractAddress, paraName, int32(addressID), int32(chainID))
	unsignTx = types.ToHex(types.Encode(tx))
	gas, err = QueryEvmGas(url, unsignTx, deployAddress)
	assert.Nil(t, err)
	fmt.Println("gas fee = ", gas)
	fee = GetProperFee(url)
	fmt.Println("proper fee = ", fee)
	UpdateTxFee(tx, gas, fee)
	// 构造交易组
	group, err := CreateNobalance(tx, useraPrivateKey, withholdPrivateKey, paraName, int32(addressID), int32(chainID))
	assert.Nil(t, err)
	txhash = types.ToHexPrefix(group.Tx().Hash())
	signTx = types.ToHexPrefix(types.Encode(group.Tx()))
	fmt.Println("tx group = ", signTx)
	reply, err = jsonclient.SendTransaction(signTx)
	assert.Nil(t, err)
	assert.Equal(t, txhash, reply)
	fmt.Print("代扣交易hash = ", txhash)
	time.Sleep(8 * time.Second)
	detail, err = jsonclient.QueryTransaction(txhash)
	assert.Nil(t, err)
	fmt.Println("; 执行结果 = ", detail.Receipt.Ty)

	// 合约查询
	param = fmt.Sprintf("balanceOf(%s,%d)", useraAddress, ids[0])
	balance, err := QueryContract(url, contractAddress, abi, param, contractAddress)
	assert.Nil(t, err)
	fmt.Println(param, " = ", balance[0])
	param = fmt.Sprintf("uri(%d)", ids[1])
	uri, err := QueryContract(url, contractAddress, abi, param, contractAddress)
	assert.Nil(t, err)
	fmt.Println(param, " = ", uri[0])
}
