evm_SignTxAndEstimate() {
    gas=0
    local txHex="$1"
    local from=$2
    local MAIN_HTTP=$3

    req='{"method":"Chain33.Query","params":[{"execer":"evm","funcName":"EstimateGas","payload":{"tx":"'${txHex}'", "from":"'${from}'"}}]}'
    chain33_Http "$req" "${MAIN_HTTP}" '(.result != null)' "EstimateGas" ".result.gas"
    gas=$((RETURN_RESP + 10000))
    echo "the estimate gas is = ${gas}"
}

#上述未签名交易使用以下指令进行创建
#./chain33-cli evm create -b erc_abi -c erc_code -n "deploy erc20" -p "" -s erc20 -f 1 --chainID 33 --paraName user.p.para.
function evm_createContract() {
    expire="120s"
    tx=$(curl -ksd '{"method":"evm.CreateDeployTx","params":[{"code":"'${erc_code}'", "abi":"'"${erc_abi}"'", "note": "deploy erc1155", "alias": "evm test", "expire":"'${expire}'", "paraName":"'"${paraName}"'", "amount":0}]}' "${MAIN_HTTP}" | jq -r ".result")
    evm_SignTxAndEstimate "${tx}" "${evm_creatorAddr}" "$MAIN_HTTP"
    echo "evm_createContract :: the estimate gas is = ${gas}"
    
    tx=$(curl -ksd '{"method":"evm.CreateDeployTx","params":[{"code":"'${erc_code}'", "abi":"'"${erc_abi}"'", "fee":'${gas}', "note": "deploy erc1155", "alias": "evm test", "expire":"'${expire}'", "paraName":"'"${paraName}"'", "amount":0}]}' "${MAIN_HTTP}" | jq -r ".result")
    chain33_SignAndSendTx "${tx}" "${evm_creatorAddr_key}" "$MAIN_HTTP" "${expire}" "${gas}"
    txHash=$RAW_TX_HASH

    queryTransaction "jq -r .result.receipt.tyName" "ExecOk"
    echo "CreateContract queryExecRes end"
}

# $1 parameter; $2 caller; $3 resok unpackData 匹配的数据
function evm_callQuery() {
    local parameter=$1
    local callerAddr=$2
    local resok=$3
    local name=$4

    req='{"method":"Chain33.Query","params":[{"execer":"evm","funcName":"GetPackData","payload":{"abi":"'${erc_abi}'","parameter":"'${parameter}'"}}]}'
    chain33_Http "$req" "${MAIN_HTTP}" '(.result != null)' "GetPackData" ".result.packData"
    #echo "$RETURN_RESP"

    req='{"method":"Chain33.Query","params":[{"execer":"evm","funcName":"Query","payload":{"address":"'${evm_contractAddr}'","input":"'${RETURN_RESP}'","caller":"'${callerAddr}'"}}]}'
    chain33_Http "$req" "${MAIN_HTTP}" '(.result != null)' "Query" ".result.rawData"
    #echo "$RETURN_RESP"

    req='{"method":"Chain33.Query","params":[{"execer":"evm","funcName":"GetUnpackData","payload":{"abi":"'${erc_abi}'","name":"'${name}'","data":"'${RETURN_RESP}'"}}]}'
    chain33_Http "$req" "${MAIN_HTTP}" '(.result != null)' "GetUnpackData" ".result.unpackData[0]"
    echo "$RETURN_RESP"

    [ "${RETURN_RESP}" == "${resok}" ]
    rst=$?
    echo_rst "$parameter query" "$rst"
}

function evm_initNFT() {
    expire="120s"
    ids=[10000,10001]
    amounts=[100,100]
    uris=["http://www.baidu.com","http://www.baidu.com"]

    tx=$(curl -ksd '{"method":"evm.CreateCallTx","params":[{"abi":"'"${erc_abi}"'", "note": "evm initNFT rpc test", "parameter": "mint('${userAddr}', '${ids}', '${amounts}', '${uris}')", "expire":"'${expire}'", "contractAddr":"'"${evm_contractAddr}"'", "paraName":"'"${paraName}"'"}]}' "${MAIN_HTTP}" | jq -r ".result")
    evm_SignTxAndEstimate "${tx}" "${evm_creatorAddr}" "$MAIN_HTTP"

    # shellcheck disable=SC2090
    tx=$(curl -ksd '{"method":"evm.CreateCallTx","params":[{"abi":"'"${erc_abi}"'", "fee":'${gas}', "note": "evm initNFT rpc test", "parameter": "mint('${userAddr}', '${ids}', '${amounts}', '${uris}')", "expire":"'${expire}'", "contractAddr":"'"${evm_contractAddr}"'", "paraName":"'"${paraName}"'"}]}' "${MAIN_HTTP}" | jq -r ".result")
    chain33_SignAndSendTxGroup "${tx}" "${userAddr_key}" "${withholdAddr_key}" "$MAIN_HTTP" "${expire}" "${gas}"

    evm_callQuery "balanceOf(${userAddr}, "10000")" "${evm_creatorAddr}" "100" "balanceOf"
    evm_callQuery "uri("10001")" "${evm_creatorAddr}" "http://www.baidu.com" "uri"
}

# 查询交易的执行结果
# 根据传入的规则，校验查询的结果 （参数1: 校验规则 参数2: 预期匹配结果）
function queryTransaction() {
    validators=$1
    expectRes=$2
    echo "txHash=${txHash}"

    res=$(curl -s --data-binary '{"jsonrpc":"2.0","id":2,"method":"Chain33.QueryTransaction","params":[{"hash":"'"${txHash}"'"}]}' -H 'content-type:text/plain;' "${MAIN_HTTP}")

    times=$(echo "${validators}" | awk -F '|' '{print NF}')
    for ((i = 1; i <= times; i++)); do
        validator=$(echo "${validators}" | awk -F '|' '{print $'"$i"'}')
        res=$(echo "${res}" | ${validator})
    done

    if [ "${res}" != "${expectRes}" ]; then
        return 1
    else
        res=$(curl -s --data-binary '{"jsonrpc":"2.0","id":2,"method":"Chain33.QueryTransaction","params":[{"hash":"'"${txHash}"'"}]}' -H 'content-type:text/plain;' "${MAIN_HTTP}")
        if [ "${evm_addr}" == "" ]; then
            if [ "$ispara" == false ]; then
                evm_addr="user.evm.${txHash}"
            else
                evm_addr="user.p.para.user.evm.${txHash}"
            fi
        fi

        if [ "${evm_contractAddr}" == "" ]; then
            # 去掉 hash 前面的 0x
            txhash=${txHash:2}
            evm_contractAddr=$(curl -ksd '{"method":"evm.CalcNewContractAddr","params":[{"caller":"'"${evm_creatorAddr}"'","txhash":"'"${txhash}"'"}]}' "${MAIN_HTTP}" | jq -r ".result")
            echo "contract address = ${evm_contractAddr}"
        fi
        return 0
    fi
}

echo_rst() {
    if [ "$2" -eq 0 ]; then
        echo -e "${GRE}$1 ok${NOC}"
    elif [ "$2" -eq 2 ]; then
        echo -e "${GRE}$1 not support${NOC}"
    else
        echo -e "${RED}$1 fail${NOC}"
        echo -e "${RED}$3 ${NOC}"
        CASE_ERR="err"
        echo $CASE_ERR
    fi
}

chain33_Http() {
    #  echo "#$4 request: request="$1" MAIN_HTTP="$2" js="$3" FUNCNAME="$4" response="$5""
    local body
    body=$(curl -ksd "$1" "$2")
    RETURN_RESP=$(echo "$body" | jq -r "$5")
    #echo "#response: $body" "$RETURN_RESP"
    ok=$(echo "$body" | jq -r "$3")
    [ "$ok" == true ]
    rst=$?
    echo_rst "$4" "$rst" "$body"
}

chain33_BlockWait() {
    local MAIN_HTTP=$2
    local req='"method":"Chain33.GetLastHeader","params":[]'

    cur_height=$(curl -ksd "{$req}" "${MAIN_HTTP}" | jq ".result.height")
    expect=$((cur_height + ${1}))

    local count=0
    while true; do
        new_height=$(curl -ksd "{$req}" "${MAIN_HTTP}" | jq ".result.height")
        if [ "${new_height}" -ge "${expect}" ]; then
            break
        fi
        count=$((count + 1))
        sleep 1
    done
    echo "wait new block $count/10 s, cur height=$expect,old=$cur_height"
}

chain33_QueryTx() {
    local MAIN_HTTP=$2
    chain33_BlockWait 1 "$MAIN_HTTP"
    local txhash="$1"
    local req='"method":"Chain33.QueryTransaction","params":[{"hash":"'"$txhash"'"}]'

    local times=10
    while true; do
        ret=$(curl -ksd "{$req}" "${MAIN_HTTP}" | jq -r ".result.tx.hash")
        if [ "${ret}" != "${1}" ]; then
            chain33_BlockWait 1 "$MAIN_HTTP"
            times=$((times - 1))
            if [ $times -le 0 ]; then
                echo "====query tx=$1 failed"
                curl -ksd "{$req}" "${MAIN_HTTP}"
                exit 1
            fi
        else
            RAW_TX_HASH=$txhash
            echo "====query tx=$RAW_TX_HASH success"
            break
        fi
    done
}

chain33_SendTx() {
    local signedTx=$1
    local MAIN_HTTP=$2

    req='"method":"Chain33.SendTransaction","params":[{"token":"BTY","data":"'"$signedTx"'"}]'
    resp=$(curl -ksd "{$req}" "${MAIN_HTTP}")
    err=$(jq '(.error)' <<<"$resp")
    txhash=$(jq -r ".result" <<<"$resp")

    if [ "$err" == null ]; then
        chain33_QueryTx "$txhash" "$MAIN_HTTP"
    else
        echo "send tx error:$err"
    fi
}

chain33_SignAndSendTx() {
    local txHex="$1"
    local priKey="$2"
    local MAIN_HTTP=$3
    local expire="120s"
    if [ -n "$4" ]; then
        expire=$4
    fi

    local req='"method":"Chain33.SignRawTx","params":[{"privkey":"'"$priKey"'","txHex":"'"$txHex"'","addressID":'$addrID',"expire":"'"$expire"'"}]'
    if [ -n "$5" ]; then
        fee=$5
        req='"method":"Chain33.SignRawTx","params":[{"privkey":"'"$priKey"'","txHex":"'"$txHex"'","addressID":'$addrID',"expire":"'"$expire"'","fee":'$fee'}]'
    else
        req='"method":"Chain33.SignRawTx","params":[{"privkey":"'"$priKey"'","txHex":"'"$txHex"'","addressID":'$addrID',"expire":"'"$expire"'"}]'
    fi

    signedTx=$(curl -ksd "{$req}" "${MAIN_HTTP}" | jq -r ".result")

    if [ "$signedTx" != null ]; then
        chain33_SendTx "$signedTx" "${MAIN_HTTP}"
    else
        echo "signedTx null error"
    fi
}

chain33_SignAndSendTxGroup() {
    local txHex="$1"
    local userKey="$2"
    local withholdKey="$3"
    local MAIN_HTTP=$4
    local expire=$5
    local fee=$6

     #create tx group with none
    req='"method":"Chain33.CreateNoBlanaceTxs","params":[{"txHexs":["'"$txHex"'"],"expire":"120s","fee":'$fee'}]'
    resp=$(curl -ksd "{$req}" "${MAIN_HTTP}")
    err=$(jq '(.error)' <<<"$resp")
    if [ "$err" != null ]; then
        echo "$resp"
        exit 1
    fi
    tx_hash_group=$(jq -r ".result" <<<"$resp")

    #sign
    tx_sign=$(curl -ksd '{"method":"Chain33.SignRawTx","params":[{"privkey":"'"$withholdKey"'","txHex":"'"$tx_hash_group"'","index":1,"addressID":'$addrID',"expire":"120s"}]}' "${MAIN_HTTP}" | jq -r ".result")
    tx_sign2=$(curl -ksd '{"method":"Chain33.SignRawTx","params":[{"privkey":"'"$userKey"'","txHex":"'"$tx_sign"'","index":2,"addressID":'$addrID',"expire":"120s"}]}' "${MAIN_HTTP}" | jq -r ".result")
    echo "tx group = ${tx_sign2}"

    #send
    chain33_SendTx "${tx_sign2}" "${MAIN_HTTP}"
}

function init() {
    evm_addr=$(curl -ksd '{"method":"Chain33.ConvertExectoAddr","params":[{"execname":"user.p.mbaas.evm"}]}' "${MAIN_HTTP}" | jq -r ".result")
}

function main() {
    # 部署合约
    evm_createContract

    # 调用合约
    evm_initNFT
}

main "$1"